INSERT INTO `wenda`.`comment`(`id`, `content`, `user_id`, `entity_id`, `entity_type`, `created_date`, `status`) VALUES (4698, '从那时起，意大利炮，不再是打鬼子的炮。从那时起，锅，也不再是炒菜的锅。从那时起，天才，也不再是天才。从那时起，学习，也不再重要。从那时期，异性朋友之间的关系，也有了新的定义。从那时起，男人与男人之间的友谊，也与促使某种植物开花的行为联系在了一起。从那时起，就连……，也不再是……。说了这么多，不给我点赞的同学，你这种想法很危险。这部电视剧，播出于2005年。', 8, 95, 1, '2019-05-19 12:20:24', 0);
