INSERT INTO `wenda`.`comment`(`id`, `content`, `user_id`, `entity_id`, `entity_type`, `created_date`, `status`) VALUES (4694, '以前发神经下载了个叫【泡面助手】的app功能就是  开始泡面的时候打开并选择时间 3/5/10/自定义min  倒计时结束的时候   会像微波炉那样 “叮”一声然而  根本改变不了泡面难吃的事实—————————————4.15 分割线—————————————哈？居然有人对这个感兴趣？既然都已经90赞了   能不能送我上100！小透明至今赞最多的答案了QAQ—————————————4.16 分割线—————————————噫！没想到。。。。。这么快就上100了那我发购买记录了 对。。。。这个app是我在亚马逊上面下载的  。。。。。。。没错   就是那个亚马逊不知道这个app现在变成什么样了   有兴趣的可以下载看看？', 15, 94, 1, '2019-05-19 12:20:22', 0);
