INSERT INTO `wenda`.`comment`(`id`, `content`, `user_id`, `entity_id`, `entity_type`, `created_date`, `status`) VALUES (4692, '这三个软件我已经很久没有成功启动过了，用了梯子还是不行，一直在考虑卸载掉。嗯，在大陆没什么鸟用，符合题意。还有这个除了恶臭，其他鸟用都没有', 9, 94, 1, '2019-05-19 12:20:22', 0);
