INSERT INTO `wenda`.`comment`(`id`, `content`, `user_id`, `entity_id`, `entity_type`, `created_date`, `status`) VALUES (4576, '姓资还是姓社的问题终于又浮上水面，这种舆论一旦成气候，我们就必须要做选择了，未来会怎么样，我很期待。说实话我对当年邓公的猫论一直持保留意见，固然黑猫白猫都可以是好猫，但万一那是条黄鼠狼呢？', 19, 92, 1, '2019-05-19 12:19:54', 0);
