INSERT INTO `wenda`.`comment`(`id`, `content`, `user_id`, `entity_id`, `entity_type`, `created_date`, `status`) VALUES (4503, '贴吧：一切不以交友为目的的回帖都是耍流氓，签到和抢沙发最能体现一个人的逼格豆瓣：一切不以约炮为目的的顶贴都是耍流氓，文艺和加小组最能体现一个人的逼格空间：一切不以转载为目的的看帖都是耍流氓，鸡汤和非主流最能体现一个人的逼格微信：一切不以代购为目的的发帖都是耍流氓，微商和超链接最能体现一个人的逼格知乎：一切不以集赞为目的的拼贴都是耍流氓，谢邀和公众号最能体现一个人的逼格可能总结的不是很完善，反正大致上就是那么个意思', 15, 91, 1, '2019-05-19 12:19:48', 0);
