INSERT INTO `wenda`.`comment`(`id`, `content`, `user_id`, `entity_id`, `entity_type`, `created_date`, `status`) VALUES (4324, '每看到革命领袖的音容笑貌，他语重心长的教诲就在耳边回荡。面筋哥的歌曲激励着我不要放弃追求梦想。铁骨铮铮的汉子王境泽。真香二字，掷地有声！科技界的鬼畜扛把子，一曲成名。最后当然少不了鬼畜祖师爷', 6, 87, 1, '2019-05-19 12:19:29', 0);
