INSERT INTO `wenda`.`comment`(`id`, `content`, `user_id`, `entity_id`, `entity_type`, `created_date`, `status`) VALUES (4301, '是时候祭出鬼畜全明星了。 是的 说实话 这不仅仅是有声音了 玛德还能自己脑补出一段鬼畜呢。。。---------------------------分割线---------------------------继续更。这声音简直丧心病狂啊 卧槽 分分钟想砸电脑！这个声音自己脑补吧 极其有节奏感这个你想到的是最炫民族风还是小丫小苹果滴 穴深卡我们的队伍向太阳？还是什么音乐来的？这半壁江山 没问题吧？这得有声音吧？ oh 告徳噗呲 夏天听这声音最爽了起来～ 不愿做奴隶的人们每次看到这 必然泪目-------------------------分割线 -----------------------星爷经典大笑～哈哈哈哈哈哈哈哈 损色吧我的天啊 这么神奇吗我的新歌入驻网易云了 不要脸一次希望喜欢民谣的大家去听听 http://music.163.com/m/song?id=421110855&amp;userid=102564359', 10, 87, 1, '2019-05-19 12:19:28', 0);
